  const addTask = () => {

    const newTodoList = [...todoList, newTask];
    setTodoList(newTodoList);

    const task={
      id: todoList.length == 0 ? 1 :   todoList[todoList.length-1].id+1,
      taskName: newTask,
      completed: false,
    };


    setTodoList([...todoList,task]);
    // we can use the above code block as well this is simpler and shorter way to achive the task that is
    // to add exiting list task with a newt task
  };

  // const completeTask = (id) => {
  //   setTodoList(
  //     todoList.map((task)=>{
  //       if(task.id === id){
  //         return{...task,completed:true};
  //       } 
  //       else{
  //         return task;
  //       }
  //     })
  //   )
  //   }
  const completeTask = (id) => {
    setTodoList(
      todoList.map((task) => {
        if (task.id === id) {
          return { ...task, completed: true };
        } else {
          return task;
        }
      })
    );
  };



  const deleteTask=(id)=>{
    // const newTodoList = todoList.filter((task)=> task!== taskName); 

    // const newTodoList = todoList.filter((task) =>{
    //   if (task === taskName){
    //     return false;}
    //   else{
    //     return true;}
      // })

      // setTodoList(newTodoList);
      setTodoList(todoList.filter((task)=> task.id !== id));


  };

  return (
    <div className="App">
      <div className="addTask">
        <input onChange={handleChange} /> <button onClick={(addTask)}>Add Task </button>

      </div>
      <div className="list">
        {todoList && todoList.map((task) => { return ( <Task 
          taskName={task.taskName} 
          id={task.id} 
          completed={task.completed}
          deleteTask={deleteTask} 
          completeTask={completeTask} />)
          // return (<div><h1> {task.taskName} </h1><button onClick={()=> deleteTask(task.id)}>X</button></div>);


        })}
      </div>
    </div>
  );
}

export default App;
